// src/components/Certifications.js
import React from "react";
import { motion } from "framer-motion";

const certs = [
  {
    title: "Deep Learning Specialization",
    provider: "Coursera",
    logo: "https://upload.wikimedia.org/wikipedia/commons/4/4f/Coursera-Logo_600x600.svg",
    link: "https://www.coursera.org/account/accomplishments/specialization/XYZ123"
  },
  {
    title: "Python for Data Science",
    provider: "Kaggle",
    logo: "https://upload.wikimedia.org/wikipedia/commons/7/7c/Kaggle_logo.png",
    link: "https://www.kaggle.com/learn/certificates/XYZ456"
  },
  {
    title: "Data Cleaning",
    provider: "DataCamp",
    logo: "https://cdn-images-1.medium.com/max/1600/1*mFz5xt2DR4v8ZzDqZTzG4g.png",
    link: "https://www.datacamp.com/statement-of-accomplishment/XYZ789"
  },
];

export default function Certifications() {
  return (
    <section className="container mx-auto px-6 py-20" id="certifications">
      <motion.h2
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 1 }}
        className="text-4xl font-bold text-blue-300 mb-12 text-center"
      >
        Certifications
      </motion.h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {certs.map((cert, i) => (
          <motion.a
            key={i}
            href={cert.link}
            target="_blank"
            rel="noopener noreferrer"
            whileHover={{ scale: 1.05 }}
            className="bg-gray-800 hover:bg-gray-700 transition p-6 rounded-xl shadow-xl text-center"
          >
            <img src={cert.logo} alt={cert.provider} className="h-12 mx-auto mb-4" />
            <h3 className="text-lg font-semibold text-white">{cert.title}</h3>
            <p className="text-sm text-blue-400">{cert.provider}</p>
          </motion.a>
        ))}
      </div>
    </section>
  );
}
