// src/components/AboutMe.js
import React from "react";
import { FaGithub, FaLinkedin, FaEnvelope } from "react-icons/fa";
import { motion } from "framer-motion";

export default function AboutMe() {
  return (
    <section className="bg-gradient-to-br from-purple-900 via-indigo-900 to-black py-20 px-6 text-white" id="about">
      <motion.div
        initial={{ opacity: 0, y: 50 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
        className="max-w-4xl mx-auto text-center"
      >
        <img
          src="/profile.jpg"
          alt="Yassine Chmirrou"
          className="w-40 h-40 rounded-full object-cover border-4 border-blue-500 shadow-xl mx-auto mb-6"
        />

        <h2 className="text-4xl font-bold mb-4">Yassine Chmirrou</h2>
        <p className="text-lg text-gray-300 max-w-2xl mx-auto leading-relaxed">
          I'm a final-year AI & Big Data Engineering student at ENSA TÃ©touan. Passionate about solving real-world
          problems using AI, building scalable data solutions, and bringing ideas to life with tech.
        </p>

        <div className="flex justify-center gap-6 mt-6 text-2xl">
          <a href="https://github.com/YassineChmirrou" target="_blank" rel="noopener noreferrer" className="hover:text-blue-400">
            <FaGithub />
          </a>
          <a href="https://linkedin.com/in/yassinechmirrou" target="_blank" rel="noopener noreferrer" className="hover:text-blue-400">
            <FaLinkedin />
          </a>
          <a href="mailto:yassinechmirrou1@gmail.com" className="hover:text-blue-400">
            <FaEnvelope />
          </a>
        </div>
      </motion.div>
    </section>
  );
}
