// src/components/Timeline.js
import React from "react";
import { motion } from "framer-motion";

const timelineData = [
  {
    title: "ENSA Tétouan",
    subtitle: "Engineering Degree in Big Data & AI",
    date: "2020 – 2025",
    description: "Studied advanced machine learning, big data systems, cloud technologies, and software engineering.",
  },
  {
    title: "AI Geeks Club",
    subtitle: "Vice President",
    date: "2024 – Present",
    description: "Organized AI workshops, tech talks, and collaborative hackathons to promote hands-on learning.",
  },
  {
    title: "Transparence Informatique",
    subtitle: "Data Analyst Intern",
    date: "Aug – Sep 2024",
    description: "Built dashboards in Power BI and automated Excel data pipelines during a summer internship.",
  },
  {
    title: "Personal AI Projects",
    subtitle: "Freelance Projects & Research",
    date: "2023 – Present",
    description: "Developed ML projects like food classification, cheating detection, and sentiment analysis tools.",
  },
];

export default function Timeline() {
  return (
    <section className="container mx-auto px-6 py-20" id="timeline">
      <motion.h2
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 1 }}
        className="text-4xl font-bold text-blue-300 mb-12 text-center"
      >
        My Journey
      </motion.h2>
      <div className="relative border-l-2 border-blue-500 ml-4">
        {timelineData.map((item, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5, delay: index * 0.2 }}
            className="mb-10 ml-6 relative"
          >
            <div className="absolute -left-4 w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center text-white">
              {index + 1}
            </div>
            <div className="bg-gray-800 p-6 rounded-xl shadow-xl">
              <h3 className="text-xl text-white font-semibold">{item.title}</h3>
              <h4 className="text-sm text-blue-300 font-medium mb-1">{item.subtitle}</h4>
              <span className="text-xs text-gray-400 mb-2 block">{item.date}</span>
              <p className="text-sm text-gray-300">{item.description}</p>
            </div>
          </motion.div>
        ))}
      </div>
    </section>
  );
}
