// src/components/AboutMe.js
import React, { useRef } from "react";
import { FaGithub, FaLinkedin, FaEnvelope } from "react-icons/fa";
import { motion, useMotionValue, useTransform, useSpring } from "framer-motion";
import CountUp from "react-countup";

export default function AboutMe() {
  // Tilt effect for the profile image
  const x = useMotionValue(0);
  const y = useMotionValue(0);
  const rotateX = useTransform(y, [-50, 50], [15, -15]);
  const rotateY = useTransform(x, [-50, 50], [-15, 15]);
  const springRotX = useSpring(rotateX, { stiffness: 120, damping: 14 });
  const springRotY = useSpring(rotateY, { stiffness: 120, damping: 14 });
  const imgRef = useRef(null);

  return (
    <section id="about" className="relative overflow-hidden">
      {/* Top wave separator */}
      <svg viewBox="0 0 1440 80" className="absolute top-0 w-full h-20">
        <path fill="#1F2937" d="M0,80 C480,0 960,80 1440,0 L1440,0 L0,0 Z" />
      </svg>

      {/* Animated gradient background */}
      <div className="absolute inset-0 bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-900 bg-[length:200%_200%] animate-[gradient_15s_ease_infinite] mix-blend-multiply"></div>

      <div className="relative max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-12 items-center py-24 px-6">
        {/* Profile Image with Tilt */}
        <motion.div
          ref={imgRef}
          className="w-56 h-56 mx-auto md:mx-0"
          style={{ rotateX: springRotX, rotateY: springRotY, perspective: 600 }}
          onMouseMove={(e) => {
            const rect = imgRef.current.getBoundingClientRect();
            x.set(e.clientX - rect.left - rect.width / 2);
            y.set(e.clientY - rect.top - rect.height / 2);
          }}
          onMouseLeave={() => { x.set(0); y.set(0); }}
        >
          <img
            src="/profile.jpg"
            alt="Yassine Chmirrou"
            className="w-full h-full rounded-full border-4 border-white shadow-2xl object-cover"
          />
        </motion.div>

        {/* Bio and Links */}
        <div className="space-y-6 text-center md:text-left">
          <motion.h2
            initial={{ x: -100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ duration: 0.8 }}
            className="text-4xl sm:text-5xl font-satoshi font-extrabold text-transparent bg-clip-text bg-gradient-to-r from-indigo-400 via-pink-500 to-purple-500"
          >
            Yassine Chmirrou
          </motion.h2>

          <motion.p
            initial={{ x: -100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ delay: 0.2, duration: 0.8 }}
            className="text-gray-200 leading-relaxed text-lg"
          >
            I’m a final-year AI & Big Data Engineering student at ENSA Tétouan. I build scalable data pipelines,
            train deep learning models, and turn complex data into actionable insights.
          </motion.p>

          {/* Stats with CountUp */}
          <motion.div
            initial={{ x: -100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ delay: 0.4, duration: 0.8 }}
            className="flex space-x-6 justify-center md:justify-start"
          >
            <div className="text-center">
              <span className="block text-2xl font-semibold text-blue-400">
                <CountUp end={3} duration={2} />+
              </span>
              <span className="text-sm text-gray-300">Projects</span>
            </div>
            <div className="text-center">
              <span className="block text-2xl font-semibold text-blue-400">
                <CountUp end={1} duration={2} />
              </span>
              <span className="text-sm text-gray-300">Internship</span>
            </div>
            <div className="text-center">
              <span className="block text-2xl font-semibold text-blue-400">
                <CountUp end={5} duration={2} />
              </span>
              <span className="text-sm text-gray-300">Workshops</span>
            </div>
          </motion.div>

          {/* Social links & CV download */}
          <motion.div
            initial={{ x: -100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ delay: 0.6, duration: 0.8 }}
            className="flex items-center justify-center md:justify-start space-x-6"
          >
            <a href="https://github.com/YassineChmirrou" target="_blank" rel="noopener noreferrer" className="text-2xl text-white hover:scale-110 transition-transform">
              <FaGithub />
            </a>
            <a href="https://linkedin.com/in/yassinechmirrou" target="_blank" rel="noopener noreferrer" className="text-2xl text-white hover:scale-110 transition-transform">
              <FaLinkedin />
            </a>
            <a href="mailto:yassinechmirrou1@gmail.com" className="text-2xl text-white hover:scale-110 transition-transform">
              <FaEnvelope />
            </a>
            <a
              href="/CV_YASSINE_CHMIRROU.pdf"
              className="ml-4 bg-gradient-to-r from-pink-500 to-purple-500 hover:from-pink-400 hover:to-purple-400 text-white px-5 py-2 rounded-full shadow-lg transition-transform hover:-translate-y-1"
            >
              Download CV
            </a>
          </motion.div>
        </div>
      </div>

      {/* Bottom wave separator */}
      <svg viewBox="0 0 1440 80" className="absolute bottom-0 w-full h-20">
        <path fill="#1F2937" d="M0,0 C480,80 960,0 1440,80 L1440,80 L0,80 Z" />
      </svg>
    </section>
  );
}
