// src/components/CVModal.js
import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { FaFilePdf, FaTimes } from "react-icons/fa";

export default function CVModal() {
  const [open, setOpen] = useState(false);

  return (
    <div className="text-center my-20">
      <button
        onClick={() => setOpen(true)}
        className="bg-blue-600 hover:bg-purple-600 text-white px-6 py-3 rounded-full font-semibold transition-all"
      >
        View My CV
      </button>

      <AnimatePresence>
        {open && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-80 px-4"
          >
            <motion.div
              initial={{ scale: 0.9 }}
              animate={{ scale: 1 }}
              exit={{ scale: 0.9 }}
              transition={{ duration: 0.3 }}
              className="relative bg-white rounded-lg shadow-xl overflow-hidden max-w-4xl w-full h-[90vh]"
            >
              <button
                onClick={() => setOpen(false)}
                className="absolute top-4 right-4 text-gray-600 hover:text-black text-2xl"
              >
                <FaTimes />
              </button>

              <iframe
                src="/CV_YASSINE_CHMIRROU.pdf"
                title="Yassine CV"
                className="w-full h-full"
              ></iframe>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
