// src/components/AboutMe.js
import React, { useRef } from "react";
import { FaGithub, FaLinkedin, FaEnvelope } from "react-icons/fa";
import { motion, useMotionValue, useTransform, useSpring } from "framer-motion";

export default function AboutMe() {
  // Tilt effect for the profile image
  const x = useMotionValue(0);
  const y = useMotionValue(0);
  const rotateX = useTransform(y, [-50, 50], [15, -15]);
  const rotateY = useTransform(x, [-50, 50], [-15, 15]);
  const springRotX = useSpring(rotateX, { stiffness: 120, damping: 14 });
  const springRotY = useSpring(rotateY, { stiffness: 120, damping: 14 });

  const imgRef = useRef(null);

  return (
    <section id="about" className="relative bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-900 py-24 px-4 sm:px-6 lg:px-8 overflow-hidden">
      {/* Floating decorative blobs; add animate-blob and animation-delay classes in CSS */}
      <div className="absolute top-[-4rem] left-4 w-48 h-48 bg-pink-600 opacity-20 rounded-full animate-blob mix-blend-lighten"></div>
      <div className="absolute bottom-[-4rem] right-8 w-64 h-64 bg-indigo-600 opacity-20 rounded-full animate-blob animation-delay-2000 mix-blend-lighten"></div>

      <div className="relative max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-12 items-center">
        {/* Profile Image with Tilt */}
        <motion.div
          ref={imgRef}
          className="w-56 h-56 mx-auto md:mx-0"
          style={{ rotateX: springRotX, rotateY: springRotY, perspective: 600 }}
          onMouseMove={(e) => {
            const rect = imgRef.current.getBoundingClientRect();
            x.set(e.clientX - rect.left - rect.width / 2);
            y.set(e.clientY - rect.top - rect.height / 2);
          }}
          onMouseLeave={() => { x.set(0); y.set(0); }}
        >
          <img
            src="/profile.jpg"
            alt="Yassine Chmirrou"
            className="w-full h-full rounded-full border-4 border-white shadow-2xl object-cover"
          />
        </motion.div>

        {/* Bio and Links */}
        <div className="space-y-6 text-center md:text-left">
          <motion.h2
            initial={{ x: -100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ duration: 0.8 }}
            className="text-4xl sm:text-5xl font-satoshi font-extrabold text-transparent bg-clip-text bg-gradient-to-r from-indigo-400 via-pink-500 to-purple-500"
          >
            Yassine Chmirrou
          </motion.h2>

          <motion.p
            initial={{ x: -100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ delay: 0.2, duration: 0.8 }}
            className="text-gray-200 leading-relaxed text-lg"
          >
            I’m a 4thyear AI & Big Data Engineering student at ENSA Tétouan. I love building
            scalable data pipelines, training deep learning models, and turning complex data into
            actionable insights.
          </motion.p>

          <motion.div
            initial={{ x: -100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ delay: 0.4, duration: 0.8 }}
            className="flex justify-center md:justify-start space-x-6 text-2xl text-white"
          >
            <a href="https://github.com/YassineChmirrou" target="_blank" rel="noopener noreferrer" className="hover:scale-110 transition-transform">
              <FaGithub />
            </a>
            <a href="https://linkedin.com/in/yassinechmirrou" target="_blank" rel="noopener noreferrer" className="hover:scale-110 transition-transform">
              <FaLinkedin />
            </a>
            <a href="mailto:yassinechmirrou1@gmail.com" className="hover:scale-110 transition-transform">
              <FaEnvelope />
            </a>
          </motion.div>

          <motion.a
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ delay: 0.6, duration: 0.6 }}
            href="/CV_YASSINE_CHMIRROU.pdf"
            className="inline-block mt-6 bg-gradient-to-r from-pink-500 to-purple-500 hover:from-pink-400 hover:to-purple-400 text-white px-6 py-3 rounded-full shadow-lg transition-transform hover:-translate-y-1"
          >
            Download CV
          </motion.a>
        </div>
      </div>
    </section>
  );
}
